// Dedup layer 1: filter out already-processed submissions using static data
const staticData = $getWorkflowStaticData('global');
if (!staticData.processedCodes) {
  staticData.processedCodes = [];
}

const submissions = $input.first().json.results || [];
const newSubmissions = submissions.filter(
  s => !staticData.processedCodes.includes(s.code)
);

if (newSubmissions.length === 0) {
  return []; // Nothing new, stop execution
}

// Return one item per new submission with code and event slug
return newSubmissions.map(s => ({
  json: {
    submission_code: s.code,
    event_slug: s.event || 'YOUR_PRETALX_EVENT_SLUG'
  }
}));

