// Submission data from the GET request
const submission = $input.first().json;

// Build one item per speaker
const speakers = submission.speakers.map(speaker => ({
  // Speaker data
  speaker_code: speaker.code,
  speaker_name: speaker.name,
  speaker_email: speaker.email || null,
  speaker_biography: speaker.biography,
  speaker_avatar: speaker.avatar,

  // Talk data (copied for each speaker)
  talk_code: submission.code,
  talk_title: submission.title,
  talk_abstract: submission.abstract,
  talk_track: submission.track?.en || submission.track || 'Sin track',
  talk_type: submission.submission_type?.en || submission.submission_type || 'Talk',
  talk_duration: submission.duration,
  talk_room: submission.slot?.room?.en || submission.slot?.room || 'Por definir',
  talk_start: submission.slot?.start || 'Por definir',
  talk_end: submission.slot?.end || 'Por definir',
  talk_image: submission.image,
  talk_locale: submission.content_locale || 'es',

  // Metadata for pretix
  all_speakers: submission.speakers.map(s => s.name).join(', '),
  speaker_count: submission.speakers.length,

  // Event slug for downstream API calls
  event_slug: submission.event || submission.slot?.event || 'biznagafest-2026'
}));

return speakers.map(s => ({ json: s }));

// Merge speaker email from API response with existing data
const prevData = $('Code - Split by Speaker').item.json;
const apiResponse = $input.first().json;

return [{
  json: {
    ...prevData,
    speaker_email: apiResponse.email || prevData.speaker_email
  }
}];

