// Prepare speaker data for Strapi v5 REST API
const prevData = $('Code - Merge Vouchers').item.json;

// Convert biography text to Strapi v5 richtext (blocks) format
const biographyText = prevData.speaker_biography || '';
const descriptionBlocks = biographyText
  .split('\n\n')
  .filter(p => p.trim())
  .map(paragraph => ({
    type: 'paragraph',
    children: [{ type: 'text', text: paragraph.trim() }]
  }));

// If no paragraphs, add empty one
if (descriptionBlocks.length === 0) {
  descriptionBlocks.push({
    type: 'paragraph',
    children: [{ type: 'text', text: '' }]
  });
}

// Build Strapi data payload
const strapiData = {
  name: prevData.speaker_name,
  description: descriptionBlocks,
  socials: {
    mail: prevData.speaker_email || undefined
  }
};

// Pass data as JSON string for multipart form
const item = $input.first();
item.json.strapiData = JSON.stringify({ data: strapiData });
item.json.speaker_name = prevData.speaker_name;

return [item];

// Format Strapi error for Telegram notification
const prevData = $('Code - Merge Vouchers').item.json;
const errorResponse = $input.first().json;

const errorMessage = errorResponse?.error?.message
  || errorResponse?.message
  || JSON.stringify(errorResponse).substring(0, 500);

const statusCode = errorResponse?.error?.status
  || errorResponse?.statusCode
  || 'unknown';

return [{
  json: {
    speaker_name: prevData.speaker_name,
    speaker_email: prevData.speaker_email,
    talk_title: prevData.talk_title,
    error_message: errorMessage,
    error_status: statusCode,
    raw_error: JSON.stringify(errorResponse).substring(0, 1000)
  }
}];

